x = int(input("Inserisci un numero: "))
if x < 10:
    print("Minore di 10")
elif x>10:
    print("Maggiore di 10")
else:
    print("Uguale a 10")
f=0
for f in range(x)                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        :
    print(x-f)
    f=f+1